powershell_commands:
# Get all directory roles and their members
Get-AzureADDirectoryRole | ForEach-Object {
    $role = $_
    $members = Get-AzureADDirectoryRoleMember -ObjectId $role.ObjectId
    foreach ($member in $members) {
        [PSCustomObject]@{
            RoleName = $role.DisplayName
            MemberName = $member.DisplayName
            MemberUPN = $member.UserPrincipalName
        }
    }
}

# Check for existing legacy auth blocking policies
Get-AzureADMSConditionalAccessPolicy | Where-Object {
    $_.Conditions.ClientAppTypes -contains 'other' -or 
    $_.Conditions.ClientAppTypes -contains 'exchangeActiveSync'
} | Select-Object DisplayName, State, Id

